import subprocessimport sysimport requestsfrom packaging import versionfrom YGOtoTTS.__init__ import __version__latest_release_url = "https://api.github.com/repos/{}/releases/latest".format("jansenmtan/YGOtoTTS")def get_latest_release_info():    with requests.get(latest_release_url) as response:        return response.json()def update_app():    print("Checking for update...")    latest_release_info = get_latest_release_info()    latest_version_code = latest_release_info["tag_name"]    if version.parse(__version__) >= version.parse(latest_version_code):        print("This version, {}, is the latest.".format(__version__))    elif any([sys.platform in asset["name"] for asset in latest_release_info["assets"]]):        print("Getting latest version {}...".format(latest_version_code))        for asset_possible in latest_release_info["assets"]:            if sys.platform in asset_possible["name"]:                asset = asset_possible        download_url = asset["browser_download_url"]        response = requests.get(download_url)        with open(asset["name"], "wb") as binary_file:            binary_file.write(response.content)            del response        subprocess.Popen(__file__)        # Synonymous to sys.exit        raise SystemExit