import sysimport urllib.requestimport subprocessimport requestsfrom packaging import versionfrom YGOtoTTS.__init__ import __version__latest_release_url = "https://api.github.com/repos/{}/releases/latest".format("jansenmtan/YGOtoTTS")def get_latest_release_info():    with requests.get(latest_release_url) as response:        return response.json()def update_app(executable_path_old):    print("Checking for update...")    latest_release_info = get_latest_release_info()    latest_version_code = latest_release_info["tag_name"]    if version.parse(__version__) >= version.parse(latest_version_code):        print("This version, {}, is the latest.".format(__version__))    elif any([sys.platform in asset["name"] for asset in latest_release_info["assets"]]):        print("Getting latest version {}...".format(latest_version_code))        for asset in latest_release_info["assets"]:            if sys.platform in asset["name"]:                urllib.request.urlretrieve(asset["browser_download_url"], asset["name"])                print("Got the latest version.")                subprocess.Popen("{} -d {}".format(asset["name"], executable_path_old))                raise SystemExit